package com.playground.api.assets.entity;import java.math.BigDecimal;import com.playground.entity.BaseEntity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.EqualsAndHashCode;import lombok.Getter;import lombok.RequiredArgsConstructor;/** * * com.playground.api.Assets.entity * AssetsEntity.java *  * @author      :  * @Date        : 2024. 5. 27. * @Description : 자산 관리 테이블 */@Builder@AllArgsConstructor@RequiredArgsConstructor@EqualsAndHashCode(callSuper = true)@Getter@Entity@Table (name = "tb_assets")public class AssetsEntity extends BaseEntity {   /**    * 자산일련변호    */   @Id   @GeneratedValue(strategy = GenerationType.IDENTITY)   @Column(name = "assets_sn")   private Integer assetsSn;   /**    * 자산종류코드    */   @Column(name = "assets_knd_code")   private String assetsKndCode;   /**    * 자산명    */   @Column(name = "assets_nm")   private String assetsNm;   /**    * 자산설명    */   @Column(name = "assets_dc")   private String assetsDc;   /**    * 자산금액    */   @Column(name = "assets_amount")   private BigDecimal assetsAmount;   /**    * 사용여부    */   @Column(name = "use_at")   private String useAt;}
