package com.playground.api.author.entity;import com.playground.entity.BaseEntity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.EqualsAndHashCode;import lombok.Getter;import lombok.RequiredArgsConstructor;/** * * com.playground.api.Author.entity AuthorEntity.java *  * @author : * @Date : 2024. 5. 20. * @Description : 권한 Table Entity */@Builder@AllArgsConstructor@RequiredArgsConstructor@EqualsAndHashCode(callSuper = true)@Getter@Entity@Table(name = "tb_author")public class AuthorEntity extends BaseEntity {  /**   * 권한ID   */  @Id  @Column(name = "author_id")  private String authorId;  /**   * 권한명   */  @Column(name = "author_nm")  private String authorNm;  /**   * 삭제여부   */  @Column(name = "delete_at")  private String deleteAt;}