package com.playground.api.hashtag.entity;import com.playground.entity.BaseEntity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.GeneratedValue;import jakarta.persistence.GenerationType;import jakarta.persistence.Id;import jakarta.persistence.Table;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.EqualsAndHashCode;import lombok.Getter;import lombok.RequiredArgsConstructor;/** * * com.playground.api.Hashtag.entity * HashtagEntity.java *  * @author      :  * @Date        : 2024. 4. 29. * @Description : 해시태그 Table Entity */@Builder@AllArgsConstructor@RequiredArgsConstructor@EqualsAndHashCode(callSuper = true)@Getter@Entity@Table (name = "tb_hashtag")public class HashtagEntity extends BaseEntity {   /**    * 해시태그일련번호    */   @Id   @GeneratedValue(strategy = GenerationType.IDENTITY)   @Column(name = "hashtag_sn")   private Integer hashtagSn;   /**    * 해시태그명    */   @Column(name = "hashtag_nm")   private String hashtagNm;}
